print(sum(map(lambda x: ord(x)-96 if x >= 'a' and x <= 'z' else ord(x)-38, [list(set(open('input.txt', 'r').read().split('\n')[i]).intersection(set(open('input.txt', 'r').read().split('\n')[i+1])).intersection(set(open('input.txt', 'r').read().split('\n')[i+2])))[0] for i in range(0, len(open('input.txt', 'r').read().split('\n')), 3)])))
